import React from "react"
import appRouter from "./router"
import languageKeys from "./stores/i18n-keys"
// import LanguageFilter from "./stores/i18n-filter"
import FacetedSearch from "./hire-faceted-search-elab"
import Document from "./components/document-controller"
import appStore from "./app-store"
import {setResults} from "./actions/view"


class AppController extends React.Component {

	constructor(props) {
		super(props);
		this.state = appStore.getState();
		this.cachedViews = {
			search: {}
		};
		this.themesSet = false;
		this.themeQuerySet = false;
	}

	/* eslint react/no-did-mount-set-state: 0 */
	componentDidMount() {
		this.unsubscribe = appStore.subscribe(() =>
			// This is not a call to setState in componentDidMount by in the appStore subscription
			this.setState(appStore.getState())
		);
	}

	componentWillUnmount() {
		this.unsubscribe();
	}


	navigateHome() {
		appRouter.navigate("/" + this.state.language.current + "/search");
	}

	navigateLanguage(ev) {
		if( appRouter.history.fragment === "") {
			appRouter.navigate(ev.target.getAttribute("data-lang") + "/search");
		} else {
			let path = appRouter.history.fragment.replace(/^[a-z]{2}/, ev.target.getAttribute("data-lang"));
			appRouter.navigate(path);
		}
	}

	navigateToResult(obj) {
		window.scrollTo(0, 0);
		appRouter.navigateToResult({id: obj.id});
	}

	onResultsChange(data) {
		if(this.props.presetThemes && !this.themesSet) {
			appStore.dispatch({type: "SET_THEMES", themes: this.props.presetThemes});
			this.themesSet = true;
		}
		appStore.dispatch(setResults(data));
	}

	renderFacetedSearch(lang) {
		if(this.cachedViews.search[lang] && (!this.state.controller.query || this.themeQuerySet)) {
			return this.cachedViews.search[lang]
		}

		if(this.state.controller.query) this.themeQuerySet = true

		// let facetList = new LanguageFilter(lang, Object.keys(languageKeys[lang].facetTitles));
		let facetList = [
			"Locatie",
			"Datum",
			"Boekjaar",
			"Historische instelling",
			"Inventaris nummer",
			"Document type", // visible in the Dutch version
			"Document type ENG", // visible in the English and Spanish version
			"Folio nummer(s)",
			"Pagina nummer(s)",
			"Toegangsnummer",
			"Titel",
			"Auteur",
			"Plaats van publicatie",
			"Jaar van publicatie",
			"Genoemde personen",
			"Thema's"
		];

		this.cachedViews.search[lang] = (
			<FacetedSearch
				config={this.props.config}
				facetList={facetList}
				labels={languageKeys[lang]}
				onChange={this.onResultsChange.bind(this)}
				onSelect={this.navigateToResult.bind(this)}
				query={this.state.controller.query} />
		);

		return this.cachedViews.search[lang];
	}

	onThumbSelect(id) {
		this.navigateToResult({id: id});
	}

	render() {
		let doc = this.state.controller.id ?
			<Document
					activeTab={this.state.controller.activeTab}
					annotationId={this.state.controller.annotationId}
					data={this.state.controller.data}
					id={this.state.controller.id}
					language={this.state.language.current}
					pages={this.state.results}
					/> :
			null;

		if(this.state.controller.current === "document") {
			document.querySelector("body").className = "document";
		} else {
			document.querySelector("body").className = "";
		}
		return (
			<div className={this.state.controller.current === "document" ? "app document" : "app"}>
				<header>
					<a onClick={this.navigateHome.bind(this)}>
						<img className="brcp" src="data:image/png;base64," />
						<h1>BoschDoc</h1>
					</a>
					<div className="logo-wrap">
						<img className="sbosch" src="data:image/png;base64," />
						<img className="sbosch-small" src="data:image/png;base64," />
						<img className="radboud" src="data:image/png;base64," />
						<img className="radboud-small" src="data:image/png;base64," />
					</div>
					<img
						className="hi-logo"
						height="66px"
						src="https://huygens.knaw.nl/wp-content/themes/huygens/img/logo-blue.svg"
						width="92px"
					/>
					<nav>
						<a className={this.state.language.current === "nl" ? "selected" : null} data-lang="nl" onClick={this.navigateLanguage.bind(this)} >
							Ned
						</a>&nbsp;
						<a className={this.state.language.current === "en" ? "selected" : null} data-lang="en" onClick={this.navigateLanguage.bind(this)} >
							Eng
						</a>&nbsp;
						<a className={this.state.language.current === "es" ? "selected" : null} data-lang="es" onClick={this.navigateLanguage.bind(this)} >
							Esp
						</a>
					</nav>
				</header>
				<div className="entry-wrapper" style={this.state.controller.current === "document" ? {display: "block"} : {display: "none"}}>
					{doc}
				</div>
				<div style={this.state.controller.current === "search" ? {display: "block"} : {display: "none"}}>
					{this.renderFacetedSearch(this.state.language.current)}
				</div>
			</div>
		);
	}
}

export default AppController
